mapper.update:
UPDATE "FILE"
SET HIT = HIT + 1
WHERE FILEID = #{fileID}

UPDATE "FILE"
SET DELETEDATE = SYSDATE
WHERE FILEID = #{fileID}

UPDATE "FILE"
SET SUBJECT    = #{subject}
  , CONTENT    = #{content}
  , NOTICE     = #{notice}
  , IP         = #{ip}
  , UPDATEDATE = SYSDATE
WHERE FILEID = #{fileID}
